UPDATE CONSTELLATIONS SET description = '<b>Cancer</b> is one of the twelve <a title="Constellation" href="wiki/Constellation">constellations</a> of the <a title="Zodiac" href="wiki/Zodiac">zodiac</a>. Its name is <a title="Latin" href="wiki/Latin">Latin</a> for <a title="Crab" href="wiki/Crab">crab</a> and it is commonly represented as such. Its symbol is <a class="image" href="wiki/File:Cancer_glyph.png"><img height="15" alt="Cancer glyph.png" width="20" src="//upload.wikimedia.org/wikipedia/en/thumb/f/f7/Cancer_glyph.png/20px-Cancer_glyph.png"></img></a> (<span class="Unicode">Unicode &#x264b;</span>). Cancer is small and its stars are faint. It lies between <a title="Gemini (constellation)" href="wiki/Gemini_(constellation)">Gemini</a> to the west and <a title="Leo (constellation)" href="wiki/Leo_(constellation)">Leo</a> to the east, <a title="Lynx (constellation)" href="wiki/Lynx_(constellation)">Lynx</a> to the north and <a title="Canis Minor" href="wiki/Canis_Minor">Canis Minor</a> and <a title="Hydra (constellation)" href="wiki/Hydra_(constellation)">Hydra</a> to the south.' WHERE code = 'cnc';
UPDATE CONSTELLATIONS SET description = '<b>Columba</b> is a small, faint <a title="Constellation" href="wiki/Constellation">constellation</a> created in the late sixteenth century. Its name is <a title="Latin" href="wiki/Latin">Latin</a> for <a title="Dove" class="mw-redirect" href="wiki/Dove">dove</a>. It is located just south of <a title="Canis Major" href="wiki/Canis_Major">Canis Major</a> and <a title="Lepus (constellation)" href="wiki/Lepus_(constellation)">Lepus</a>.' WHERE code = 'col';
UPDATE CONSTELLATIONS SET description = '<b>Coma Berenices</b> is a traditional <a title="Asterism (astronomy)" href="wiki/Asterism_(astronomy)">asterism</a> that has since been defined as one of the 88 modern <a title="Constellation" href="wiki/Constellation">constellations</a>. It is located near <a title="Leo (constellation)" href="wiki/Leo_(constellation)">Leo</a>, to which it formerly belonged, and accommodates the <a title="North Galactic Pole" class="mw-redirect" href="wiki/North_Galactic_Pole">North Galactic Pole</a>. Its name means "Berenice"s Hair" (in <a title="Greek language" href="wiki/Greek_language">Greek</a>, via <a title="Latin" href="wiki/Latin">Latin</a>), and refers to the legend of Queen <a title="Berenice II of Egypt" class="mw-redirect" href="wiki/Berenice_II_of_Egypt">Berenice II of Egypt</a>, who sacrificed her long hair.' WHERE code = 'com';
